import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3000');

function App() {
  const [comments, setComments] = useState<string[]>([]);
  const [newComment, setNewComment] = useState<string>('');

  useEffect(() => {
    // listen for incoming comments from the server
    socket.on('comment', (msg: string) => {
      setComments((comments) => [...comments, msg]);
    });

    return () => {
      // disconnect from the server when the component unmounts
      socket.disconnect();
    };
  }, []);

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    socket.emit('comment', newComment);
    console.log(newComment);
    setNewComment('');
  };

  return (
    <div>
      <h1>Realtime Comments</h1>
      <ul>
        {comments.map((comment, index) => (
          <li key={index}>{comment}</li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
